// ============================================================
// CART — stored in localStorage so it persists across pages
// ============================================================
const CART_KEY = 'maison_cart';

function getCart()        { return JSON.parse(localStorage.getItem(CART_KEY) || '[]'); }
function saveCart(cart)   { localStorage.setItem(CART_KEY, JSON.stringify(cart)); }

function addToCart(product) {
  const cart = getCart();
  const hit  = cart.find(i => i.id === product.id);
  if (hit) { hit.qty += 1; } else { cart.push({ ...product, qty: 1 }); }
  saveCart(cart);
  updateCartCount();
  flashNotif(product.name);
  openCart();
}

function removeFromCart(id) {
  saveCart(getCart().filter(i => i.id !== id));
  renderCartItems();
  updateCartCount();
}

function changeQty(id, delta) {
  const cart = getCart();
  const item = cart.find(i => i.id === id);
  if (!item) return;
  item.qty += delta;
  if (item.qty <= 0) return removeFromCart(id);
  saveCart(cart);
  renderCartItems();
  updateCartCount();
}

function updateCartCount() {
  const total = getCart().reduce((s, i) => s + i.qty, 0);
  document.querySelectorAll('.cart-count').forEach(el => {
    el.textContent = total;
    el.classList.toggle('hidden', total === 0);
  });
}

function renderCartItems() {
  const cart  = getCart();
  const body  = document.getElementById('cart-body');
  const total = document.getElementById('cart-total');
  if (!body) return;

  if (!cart.length) {
    body.innerHTML = '<p class="text-center text-sm py-16" style="color:#9E8E82">Your cart is empty.</p>';
    if (total) total.textContent = '\u20AC0.00';
    return;
  }

  body.innerHTML = cart.map(item => `
    <div class="flex gap-3 py-4 border-b border-gray-100">
      <img src="${item.image}" alt="${item.name}" class="w-20 h-24 object-cover flex-shrink-0" />
      <div class="flex-1 min-w-0">
        <p class="text-sm font-medium leading-tight" style="font-family:'Cormorant Garamond',serif;font-size:1rem">${item.name}</p>
        <p class="text-xs mt-1" style="color:#9E8E82">${item.category}</p>
        <div class="flex items-center gap-2 mt-2">
          <button onclick="changeQty('${item.id}',-1)" class="w-6 h-6 border text-xs flex items-center justify-center hover:bg-gray-900 hover:text-white transition-colors">−</button>
          <span class="text-sm w-5 text-center">${item.qty}</span>
          <button onclick="changeQty('${item.id}',1)"  class="w-6 h-6 border text-xs flex items-center justify-center hover:bg-gray-900 hover:text-white transition-colors">+</button>
        </div>
        <p class="text-sm mt-2">\u20AC${((item.salePrice || item.price) * item.qty).toFixed(2)}</p>
      </div>
      <button onclick="removeFromCart('${item.id}')" class="text-xl self-start leading-none" style="color:#9E8E82">×</button>
    </div>`).join('');

  const sum = cart.reduce((s, i) => s + (i.salePrice || i.price) * i.qty, 0);
  if (total) total.textContent = `\u20AC${sum.toFixed(2)}`;
}

function openCart() {
  document.getElementById('cart-sidebar')?.classList.add('cart-open');
  document.getElementById('cart-overlay')?.classList.add('overlay-show');
  renderCartItems();
}
function closeCart() {
  document.getElementById('cart-sidebar')?.classList.remove('cart-open');
  document.getElementById('cart-overlay')?.classList.remove('overlay-show');
}

function flashNotif(name) {
  const n = document.getElementById('notif');
  if (!n) return;
  n.textContent = `"${name}" added to cart`;
  n.classList.remove('opacity-0', 'translate-y-1');
  n.classList.add('opacity-100', 'translate-y-0');
  clearTimeout(n._t);
  n._t = setTimeout(() => {
    n.classList.remove('opacity-100','translate-y-0');
    n.classList.add('opacity-0','translate-y-1');
  }, 2800);
}

// ============================================================
// PRODUCT CARD RENDERER — used by category + homepage
// ============================================================
function productCard(p) {
  const displayPrice = p.salePrice
    ? `<span class="line-through mr-2" style="color:#9E8E82">\u20AC${p.price}</span><span style="color:#c0392b">\u20AC${p.salePrice}</span>`
    : `\u20AC${p.price}`;
  return `
    <article class="group cursor-pointer product-card">
      <a href="/product/detail.html?id=${p.id}" class="block overflow-hidden bg-gray-50 aspect-[4/5]">
        <figure>
          <img src="${p.image}" alt="${p.name}"
            class="w-full h-full object-cover transition-transform duration-700 group-hover:scale-105" loading="lazy" />
        </figure>
      </a>
      <div class="mt-3 flex justify-between items-start gap-2">
        <div>
          <a href="/product/detail.html?id=${p.id}" class="block text-sm hover:underline" style="font-family:'Cormorant Garamond',serif;font-size:1.05rem">${p.name}</a>
          <p class="text-xs mt-0.5" style="color:#9E8E82">${p.category}</p>
        </div>
        <p class="text-sm whitespace-nowrap">${displayPrice}</p>
      </div>
      <button onclick='addToCart(${JSON.stringify(p)})'
        class="mt-2 w-full py-2 text-xs tracking-widest uppercase border border-gray-900 hover:bg-gray-900 hover:text-white transition-colors duration-200">
        Add to Cart
      </button>
    </article>`;
}

